import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  makeStyles,
} from '@material-ui/core';
import {
  AccountBalance as AccountBalanceIcon,
  SwapHoriz as SwapHorizIcon,
  History as HistoryIcon,
} from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginTop: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(3),
    textAlign: 'center',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    cursor: 'pointer',
    transition: 'transform 0.2s',
    '&:hover': {
      transform: 'scale(1.02)',
    },
  },
  icon: {
    fontSize: 48,
    marginBottom: theme.spacing(2),
    color: theme.palette.primary.main,
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  description: {
    marginBottom: theme.spacing(2),
    flexGrow: 1,
  },
  button: {
    marginTop: 'auto',
  },
}));

function Dashboard() {
  const classes = useStyles();
  const navigate = useNavigate();

  const features = [
    {
      title: 'Transfer Funds',
      description: 'Send money to other banks with optimal routes for cost and time',
      icon: <SwapHorizIcon className={classes.icon} />,
      path: '/transfer',
    },
    {
      title: 'Transaction History',
      description: 'View your past transactions and their status',
      icon: <HistoryIcon className={classes.icon} />,
      path: '/history',
    },
    {
      title: 'Bank Information',
      description: 'View information about connected banks and their services',
      icon: <AccountBalanceIcon className={classes.icon} />,
      path: '/banks',
    },
  ];

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom align="center">
        Welcome to Fund Transfer System
      </Typography>
      <Grid container spacing={4}>
        {features.map((feature) => (
          <Grid item xs={12} sm={6} md={4} key={feature.title}>
            <Paper
              className={classes.paper}
              onClick={() => navigate(feature.path)}
            >
              {feature.icon}
              <Typography variant="h6" className={classes.title}>
                {feature.title}
              </Typography>
              <Typography
                variant="body2"
                color="textSecondary"
                className={classes.description}
              >
                {feature.description}
              </Typography>
              <Button
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Go to {feature.title}
              </Button>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Dashboard;
