import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  makeStyles,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
} from '@material-ui/core';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(2),
  },
  table: {
    minWidth: 650,
  },
  statusPending: {
    backgroundColor: '#fff3cd',
  },
  statusCompleted: {
    backgroundColor: '#d4edda',
  },
  statusFailed: {
    backgroundColor: '#f8d7da',
  },
}));

function TransactionHistory() {
  const classes = useStyles();
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(
          'http://localhost:5000/api/transactions/user/me',
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTransactions(response.data);
      } catch (error) {
        setError('Failed to fetch transactions');
      }
    };
    fetchTransactions();
  }, []);

  const getStatusChipColor = (status) => {
    switch (status) {
      case 'completed':
        return 'primary';
      case 'pending':
        return 'default';
      case 'failed':
        return 'secondary';
      default:
        return 'default';
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper className={classes.paper}>
        <Typography component="h1" variant="h5" gutterBottom>
          Transaction History
        </Typography>
        {error && (
          <Typography color="error" variant="body2" gutterBottom>
            {error}
          </Typography>
        )}
        <TableContainer>
          <Table className={classes.table}>
            <TableHead>
              <TableRow>
                <TableCell>Date</TableCell>
                <TableCell>From Bank</TableCell>
                <TableCell>To Bank</TableCell>
                <TableCell align="right">Amount</TableCell>
                <TableCell align="right">Cost</TableCell>
                <TableCell align="right">Time (mins)</TableCell>
                <TableCell>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {transactions.map((transaction) => (
                <TableRow key={transaction._id}>
                  <TableCell>
                    {new Date(transaction.createdAt).toLocaleDateString()}
                  </TableCell>
                  <TableCell>{transaction.sourceBank.bankName}</TableCell>
                  <TableCell>{transaction.destinationBank.bankName}</TableCell>
                  <TableCell align="right">${transaction.amount}</TableCell>
                  <TableCell align="right">${transaction.transferCost}</TableCell>
                  <TableCell align="right">{transaction.transferTime}</TableCell>
                  <TableCell>
                    <Chip
                      label={transaction.status}
                      color={getStatusChipColor(transaction.status)}
                      size="small"
                    />
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
}

export default TransactionHistory;
